#!/usr/bin/env bash

# Analyze Retroactive Metrics Data
# Provides insights and conclusions from collected retroactive data
# Usage: ./scripts/analysis/analyze-retroactive-data.sh

set -e

SCRIPT_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"
PROJECT_ROOT="$(cd "$SCRIPT_DIR/../.." && pwd)"
RETROACTIVE_DIR="$PROJECT_ROOT/development-metrics/retroactive"

if [ ! -d "$RETROACTIVE_DIR" ]; then
    echo "Error: Retroactive data directory not found: $RETROACTIVE_DIR"
    echo "Run: ./scripts/analysis/collect-retroactive-metrics.sh first"
    exit 1
fi

echo "📊 Analyzing Retroactive Metrics Data"
echo "======================================"
echo ""

# Check if jq is available
if ! command -v jq &> /dev/null; then
    echo "Error: jq is required for analysis"
    echo "Install with: brew install jq"
    exit 1
fi

GIT_PROMPTS="$RETROACTIVE_DIR/git_prompts.json"
